import TextEditor from "./TextEditor";
import SignUp from "./SignUp";
import Login from "./Login";
import Dashboard from "./Dashboard";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect,
} from "react-router-dom";

// The main App component
function App() {
  return (
    <Router>
      <Switch>
        {/* Redirects the root path to the login page */}
        <Route path="/" exact>
          <Redirect to="/login" />
        </Route>

        {/* Renders the Login component for the login page */}
        <Route path="/login">
          <Login />
        </Route>

        {/* Renders the SignUp component for the signup page */}
        <Route path="/signup">
          <SignUp />
        </Route>

        {/* Renders the Dashboard component for the dashboard page */}
        <Route path="/dashboard">
          {localStorage.getItem("token") ? <Dashboard /> : <Redirect to="/login" />}
        </Route>
      </Switch>
    </Router>
  );
}

export default App;
